import React, { createRef, useEffect, useMemo, useRef, useState } from "react";

import Participant from "./Participant";
import SimplePeer from "simple-peer";
import Video from "twilio-video";
import VideoCall from "../helpers/simple-peer";
import create from "@ant-design/icons/lib/components/IconFont";
import { getDisplayStream } from "../helpers/media-access";

const io = require("socket.io-client");
const socket = io(process.env.reactAppSignalingServer);

const Room = ({ username, roomId, token, handleLogout }) => {
  const [room, setRoom] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [localStream, setLocalStream] = useState();
  const [remoteStreamUrl, setRemoteStreamUrl] = useState([]);
  const [streamUrl, setStreamUrl] = useState();
  const [initiator, setInitiator] = useState(false);
  const [peer, setPeer] = useState();
  const [full, setFull] = useState();
  const [connecting, setConnecting] = useState();
  const [waiting, setWaiting] = useState();
  const [localVideo, setLocalVideo] = useState();
  const [audioEnabled, setAudioEnabled] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [remoteStream, setRemoteStream] = useState([]);
  const refs = useRef(Array.from({ length: 50 }, () => React.createRef()));

  const videoCall = new VideoCall();
  const videoRef = useRef();
  const remoteRef = useRef();
  const audioRef = useRef();

  useEffect(() => {
    getUserMedia().then(() => {
      socket.emit("join", { roomId: roomId });
    });

    socket.on("init", () => {
      setInitiator(true);
    });
    socket.on("ready", () => {
      enter(roomId);
    });
    socket.on("desc", data => {
      console.log("desc");
      console.log(data.type === "offer" && initiator);
      console.log(data.type === "answer" && !initiator);
      if (data.type === "offer" && initiator) return;
      if (data.type === "answer" && !initiator) return;

      console.log("ok on appelle");
      call(data);
    });
    socket.on("disconnected", () => {
      setInitiator(true);
    });
    socket.on("full", () => {
      setFull(true);
    });
  }, [initiator]);

  const getUserMedia = cb => {
    return new Promise((resolve, reject) => {
      navigator.getUserMedia = navigator.getUserMedia =
        navigator.getUserMedia ||
        navigator.webkitGetUserMedia ||
        navigator.mozGetUserMedia;
      const op = {
        audio: true,
        video: true
      };
      navigator.getUserMedia(
        op,
        stream => {
          setStreamUrl(stream);
          setLocalStream(stream);
          setLocalVideo(stream);
          videoRef.current.srcObject = stream;

          //stream.srcObject.attach(videoRef);
          resolve();
        },
        () => {}
      );
    });
  };
  const getDisplay = () => {
    getDisplayStream().then(stream => {
      stream.oninactive = () => {
        this.state.peer.removeStream(this.state.localStream);
        this.getUserMedia().then(() => {
          this.state.peer.addStream(this.state.localStream);
        });
      };
      setStreamUrl(stream);
      setLocalStream(stream);
      localVideo.srcObject = stream;
      peer.addStream(stream);
    });
  };

  const enter = roomId => {
    setConnecting(true);
    const peer = videoCall.init(localStream, initiator);
    setPeer({ peer });

    peer.on("signal", data => {
      const signal = {
        room: roomId,
        desc: data
      };
      socket.emit("signal", signal);
    });
    peer.on("stream", stream => {
      remoteRef.current.srcObject = stream;
      setRemoteStream(stream);
      setConnecting(false);
      setWaiting(false);
    });
    peer.on("error", function(err) {
      console.log(err);
    });
  };

  const call = otherId => {
    videoCall.connect(otherId);
  };
  const renderFull = () => {
    if (this.state.full) {
      return "The room is full";
    }
  };

  const disableVideo = () => {
    const videoTrack = localStream.getVideoTracks()[0];

    if (localStream) {
      videoTrack.enabled = false;
      setVideoEnabled(false);
    }
  };

  const enableVideo = () => {
    const videoTrack = localStream.getVideoTracks()[0];

    if (localStream) {
      videoTrack.enabled = true;
      setVideoEnabled(true);
    }
  };

  return (
    <div className="room">
      <h2>Salle: {roomId}</h2>
      {connecting && (
        <div>
          <p>Establishing connection...</p>
        </div>
      )}
      {waiting && (
        <div>
          <p>Waiting for someone...</p>
        </div>
      )}
      <button onClick={handleLogout}>Déconnectation :-D</button>
      {remoteStream !== undefined ? (
        <Participant
          isLocal={false}
          key={remoteStream}
          video={
            <div>
              <video ref={remoteRef} autoPlay={true} />
            </div>
          }
        />
      ) : (
        ""
      )}
      <div className="local-participant">
        {localStream !== undefined ? (
          <Participant
            isLocal={true}
            key={localStream.id}
            name={username}
            video={
              <div>
                <video ref={videoRef} autoPlay={true} muted={!audioEnabled} />
              </div>
            }
            setAudioEnabled={setAudioEnabled}
            audioEnabled={audioEnabled}
            disableVideo={disableVideo}
            enableVideo={enableVideo}
            videoEnabled={videoEnabled}
            videoEnabled={videoEnabled}
          />
        ) : (
          ""
        )}
      </div>
    </div>
  );
};

export default Room;
