import React, { useEffect, useState } from "react";

import Participant from "./Participant";
import Video from "twilio-video";
import VideoCall from "../helpers/simple-peer";
import { getDisplayStream } from "../helpers/media-access";
import io from "socket.io-client";

const Room = ({ roomId, token, handleLogout }) => {
  const [room, setRoom] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [localStream, setLocalStream] = useState();
  const [remoteStreamUrl, setRemoteStreamUrl] = useState();
  const [streamUrl, setStreamUrl] = useState();
  const [initiator, setInitiator] = useState();
  const [peer, setPeer] = useState();
  const [full, setFull] = useState();
  const [connecting, setConnecting] = useState();
  const [waiting, setWaiting] = useState();
  const [socket, setSocket] = useState();
  const videoCall = new VideoCall();

  const remoteParticipants = participants.map(participant => (
    <Participant
      isLocal={false}
      key={participant.sid}
      participant={participant}
    />
  ));

  useEffect(() => {
    const socket = io(process.env.reactAppSignalingServer);

    setSocket(socket);

    getUserMedia().then(() => {
      socket.emit("join", { roomId: roomId });
    });
    socket.on("init", () => {
      setInitiator(true);
    });
    socket.on("ready", () => {
      console.log("c'est ok on rentre dans " + roomId);
      enter(roomId);
    });
    socket.on("desc", data => {
      if (data.type === "offer" && initiator) return;
      if (data.type === "answer" && !initiator) return;
      call(data);
    });
    socket.on("disconnected", () => {
      console.log("il est parti");
      setInitiator(true);
    });
    socket.on("full", () => {
      console.log("c'est plein");
      setFull(true);
    });

    /*const participantConnected = participant => {
      setParticipants(prevParticipants => [...prevParticipants, participant]);
    };
    const participantDisconnected = participant => {
      setParticipants(prevParticipants =>
        prevParticipants.filter(p => p !== participant)
      );
    };
    Video.connect(token, {
      name: roomName
    }).then(room => {
      setRoom(room);
      room.on("participantConnected", participantConnected);
      room.on("participantDisconnected", participantDisconnected);
      room.participants.forEach(participantConnected);
    });
    return () => {
      setRoom(currentRoom => {
        if (currentRoom && currentRoom.localParticipant.state === "connected") {
          currentRoom.localParticipant.tracks.forEach(function(
            trackPublication
          ) {
            trackPublication.track.stop();
          });
          currentRoom.disconnect();
          return null;
        } else {
          return currentRoom;
        }
      });
    };*/
  }, []);

  const enter = roomId => {
    setConnecting(true);
    const peer = this.videoCall.init(
      this.state.localStream,
      this.state.initiator
    );
    setPeer(peer);

    peer.on("signal", data => {
      const signal = {
        room: roomId,
        desc: data
      };
      this.state.socket.emit("signal", signal);
    });
    peer.on("stream", stream => {
      console.log(stream);
      this.remoteVideo.srcObject = stream;
      this.setState({ connecting: false, waiting: false });
    });
    peer.on("error", function(err) {
      console.log(err);
    });
  };
  const call = otherId => {
    this.videoCall.connect(otherId);
  };
  const renderFull = () => {
    if (this.state.full) {
      return "The room is full";
    }
  };

  return (
    <div className="room">
      <h2>Salle: {roomId}</h2>
      <button onClick={handleLogout}>DÃ©connectation :-D</button>
      {remoteParticipants}
      <div className="local-participant">
        {room ? (
          <Participant
            isLocal={true}
            key={room.localParticipant.sid}
            participant={room.localParticipant}
          />
        ) : (
          ""
        )}
      </div>
    </div>
  );
};

export default Room;
