import {
  AudioMutedOutlined,
  AudioOutlined,
  VideoCameraAddOutlined,
  VideoCameraOutlined
} from "@ant-design/icons";
import React, { useEffect, useRef, useState } from "react";

import { Button } from "antd";

const Participant = ({ participant, isLocal }) => {
  const [videoTracks, setVideoTracks] = useState([]);
  const [audioTracks, setAudioTracks] = useState([]);
  const [audioEnabled, setAudioEnabled] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const videoRef = useRef();
  const audioRef = useRef();
  const trackpubsToTracks = trackMap =>
    Array.from(trackMap.values())
      .map(publication => publication.track)
      .filter(track => track !== null);
  useEffect(() => {
    const trackSubscribed = track => {
      if (track.kind === "video") {
        setVideoTracks(videoTracks => [...videoTracks, track]);
      } else {
        setAudioTracks(audioTracks => [...audioTracks, track]);
      }
    };

    const trackUnsubscribed = track => {
      if (track.kind === "video") {
        setVideoTracks(videoTracks => videoTracks.filter(v => v !== track));
      } else {
        setAudioTracks(audioTracks => audioTracks.filter(a => a !== track));
      }
    };
    setVideoTracks(trackpubsToTracks(participant.videoTracks));
    setAudioTracks(trackpubsToTracks(participant.audioTracks));

    participant.on("trackSubscribed", trackSubscribed);
    participant.on("trackUnsubscribed", trackUnsubscribed);

    return () => {
      setVideoTracks([]);
      setAudioTracks([]);
      participant.removeAllListeners();
    };
  }, [participant]);

  useEffect(() => {
    const videoTrack = videoTracks[0];
    const audioTrack = audioTracks[0];
    if (videoTrack && audioTrack) {
      videoTrack.attach(videoRef.current);
      audioTrack.attach(audioRef.current);
      return () => {
        videoTrack.detach();
        audioTrack.detach();
      };
    }
  }, [videoTracks]);

  const getAudioButtons = () => {
    console.log("audio buttons");
    if (isLocal === true) {
      if (audioEnabled) {
        return (
          <Button
            onClick={e => setAudioEnabled(false)}
            style={{ position: "relative" }}
            type="primary"
            shape="circle"
            icon={<AudioMutedOutlined />}
          />
        );
      } else {
        return (
          <Button
            onClick={e => setAudioEnabled(true)}
            style={{ position: "relative", color: "red" }}
            type="primary"
            shape="circle"
            icon={<AudioOutlined />}
          />
        );
      }
    }
    return <div></div>;
  };

  const getVideoButtons = () => {
    if (isLocal === true) {
      if (videoEnabled) {
        return (
          <Button
            onClick={e => {
              setVideoEnabled(false);
              const videoTrack = videoTracks[0];
              if (videoTrack) {
                videoTrack.enable(false);
                videoTrack.attach(videoRef.current);
              }
            }}
            style={{ position: "relative" }}
            type="primary"
            shape="circle"
            icon={<VideoCameraOutlined />}
          />
        );
      } else {
        return (
          <Button
            onClick={e => {
              setVideoEnabled(true);
              const videoTrack = videoTracks[0];
              if (videoTrack) {
                videoTrack.enable(true);
                videoTrack.attach(videoRef.current);
              }
            }}
            style={{ position: "relative", color: "red" }}
            type="primary"
            shape="circle"
            icon={<VideoCameraOutlined />}
          />
        );
      }
    }
    return <div></div>;
  };
  return (
    <div className="participant">
      <h3>
        {participant.identity}
        {isLocal ? "(moi)" : ""}
      </h3>
      <video ref={videoRef} autoPlay={videoEnabled} />
      <audio ref={audioRef} autoPlay={true} muted={!audioEnabled} />

      {getAudioButtons()}
      {getVideoButtons()}
    </div>
  );
};

export default Participant;
