import React, { useEffect, useState } from "react";

import Participant from "./Participant";
import { Rnd } from "react-rnd";
import Video from "twilio-video";

const Room = ({ roomName, token, handleLogout }) => {
  const [room, setRoom] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [widthLocal, setWidthLocal] = useState(1500);
  const [heightLocal, setHeighLocal] = useState(1500);
  const [xLocal, setXLocal] = useState(0);
  const [yLocal, setYLocal] = useState(0);
  const remoteParticipants = participants.map(participant => (
    <Participant
      isLocal={false}
      key={participant.sid}
      participant={participant}
    />
  ));

  useEffect(() => {
    const participantConnected = participant => {
      setParticipants(prevParticipants => [...prevParticipants, participant]);
    };
    const participantDisconnected = participant => {
      setParticipants(prevParticipants =>
        prevParticipants.filter(p => p !== participant)
      );
    };
    Video.connect(token, {
      name: roomName
    }).then(room => {
      setRoom(room);
      room.on("participantConnected", participantConnected);
      room.on("participantDisconnected", participantDisconnected);
      room.participants.forEach(participantConnected);
    });
    return () => {
      setRoom(currentRoom => {
        if (currentRoom && currentRoom.localParticipant.state === "connected") {
          currentRoom.localParticipant.tracks.forEach(function(
            trackPublication
          ) {
            trackPublication.track.stop();
          });
          currentRoom.disconnect();
          return null;
        } else {
          return currentRoom;
        }
      });
    };
  }, [roomName, token]);

  return (
    <div className="room">
      <h2>Salle: {roomName}</h2>
      <button onClick={handleLogout}>DÃ©connectation :-D</button>
      {remoteParticipants}
      <div className="local-participant">
        {room ? (
          <Rnd
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              border: "solid 1px #ddd",
              background: "#f0f0f0"
            }}
            size={{ width: widthLocal, height: heightLocal }}
            position={{ x: xLocal, y: yLocal }}
            onDragStop={(e, d) => {
              setXLocal(d.x);
              setYLocal(d.y);
            }}
            onResizeStop={(e, direction, ref, delta, position) => {
              setWidthLocal(ref.offsetWidth);
              setHeighLocal(ref.offsetHeight);
              console.log(position);
            }}
          >
            <Participant
              isLocal={true}
              key={room.localParticipant.sid}
              participant={room.localParticipant}
            />
          </Rnd>
        ) : (
          ""
        )}
      </div>
    </div>
  );
};

export default Room;
