import React, { useEffect, useRef, useState } from "react";

import Participant from "./Participant";
import SimplePeer from "simple-peer";
import Video from "twilio-video";
import VideoCall from "../helpers/simple-peer";
import { getDisplayStream } from "../helpers/media-access";

const io = require("socket.io-client");
const socket = io(process.env.reactAppSignalingServer);

const Room = ({ username, roomId, token, handleLogout }) => {
  const [room, setRoom] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [localStream, setLocalStream] = useState();
  const [remoteStreamUrl, setRemoteStreamUrl] = useState([]);
  const [streamUrl, setStreamUrl] = useState();
  const [initiator, setInitiator] = useState();
  const [peer, setPeer] = useState();
  const [full, setFull] = useState();
  const [connecting, setConnecting] = useState();
  const [waiting, setWaiting] = useState();
  const [localVideo, setLocalVideo] = useState();
  const [audioEnabled, setAudioEnabled] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [peers, setPeers] = useState([]);

  const videoCall = new VideoCall();
  const videoRef = useRef();
  const audioRef = useRef();

  const remoteParticipants = participants.map(participant => (
    <Participant
      isLocal={false}
      key={participant.sid}
      participant={participant}
    />
  ));

  useEffect(() => {
    console.log(socket);

    getUserMedia().then(() => {
      console.log("join");
      socket.emit("join", { roomId: roomId });

      console.log("emit join");
    });
    socket.on("peer", msg => {
      const peerId = msg.peerId;
      const socketId = socket.id;
      console.log(msg.peerId);
      console.log(socket.id);

      if (peerId === socketId) {
        console.log("C'est du local");
        return;
      } else {
        console.log("on va crÃ©er le guss");
        setConnecting(true);
        const peer = videoCall.init(localStream, initiator);
        setPeer(peer);

        peer.on("signal", data => {
          console.log("signal");
          const signal = {
            room: roomId,
            desc: data
          };
          socket.emit("signal", signal);
        });
        peer.on("stream", stream => {
          console.log("stream");
          //this.remoteVideo.srcObject = stream;
          setConnecting(false);
          setWaiting(false);
        });
        peer.on("error", function(err) {
          console.log(err);
        });
      }
    });
    socket.on("init", () => {
      console.log("init");
      setInitiator(true);
    });
    socket.on("ready", () => {
      console.log("c'est ok on rentre dans " + roomId);
      //enter(roomId);
    });
    socket.on("desc", data => {
      console.log("desc");
      if (data.type === "offer" && initiator) return;
      if (data.type === "answer" && !initiator) return;
      call(data);
    });
    socket.on("disconnected", () => {
      console.log("il est parti");
      setInitiator(true);
    });
    socket.on("full", () => {
      console.log("c'est plein");
      setFull(true);
    });

    /*const participantConnected = participant => {
      setParticipants(prevParticipants => [...prevParticipants, participant]);
    };
    const participantDisconnected = participant => {
      setParticipants(prevParticipants =>
        prevParticipants.filter(p => p !== participant)
      );
    };
    Video.connect(token, {
      name: roomName
    }).then(room => {
      setRoom(room);
      room.on("participantConnected", participantConnected);
      room.on("participantDisconnected", participantDisconnected);
      room.participants.forEach(participantConnected);
    });
    return () => {
      setRoom(currentRoom => {
        if (currentRoom && currentRoom.localParticipant.state === "connected") {
          currentRoom.localParticipant.tracks.forEach(function(
            trackPublication
          ) {
            trackPublication.track.stop();
          });
          currentRoom.disconnect();
          return null;
        } else {
          return currentRoom;
        }
      });
    };*/
  }, []);

  const getUserMedia = cb => {
    return new Promise((resolve, reject) => {
      navigator.getUserMedia = navigator.getUserMedia =
        navigator.getUserMedia ||
        navigator.webkitGetUserMedia ||
        navigator.mozGetUserMedia;
      const op = {
        audio: true,
        video: true
      };
      navigator.getUserMedia(
        op,
        stream => {
          console.log(op);
          console.log(stream);
          setStreamUrl(stream);
          setLocalStream(stream);
          setLocalVideo(stream);
          videoRef.current.srcObject = stream;

          //stream.srcObject.attach(videoRef);
          resolve();
        },
        () => {}
      );
    });
  };
  const getDisplay = () => {
    getDisplayStream().then(stream => {
      stream.oninactive = () => {
        this.state.peer.removeStream(this.state.localStream);
        this.getUserMedia().then(() => {
          this.state.peer.addStream(this.state.localStream);
        });
      };
      setStreamUrl(stream);
      setLocalStream(stream);
      localVideo.srcObject = stream;
      peer.addStream(stream);
    });
  };

  const call = otherId => {
    videoCall.connect(otherId);
  };
  const renderFull = () => {
    if (this.state.full) {
      return "The room is full";
    }
  };

  const disableVideo = () => {
    const videoTrack = localStream.getVideoTracks()[0];

    if (localStream) {
      videoTrack.enabled = false;
      setVideoEnabled(false);
    }
  };

  const enableVideo = () => {
    const videoTrack = localStream.getVideoTracks()[0];

    if (localStream) {
      videoTrack.enabled = true;
      setVideoEnabled(true);
    }
  };

  return (
    <div className="room">
      <h2>Salle: {roomId}</h2>
      {connecting && (
        <div>
          <p>Establishing connection...</p>
        </div>
      )}
      {waiting && (
        <div>
          <p>Waiting for someone...</p>
        </div>
      )}
      <button onClick={handleLogout}>DÃ©connectation :-D</button>

      <div className="local-participant">
        {localStream !== undefined ? (
          <Participant
            isLocal={true}
            key={localStream.id}
            name={username}
            video={
              <div>
                <video ref={videoRef} autoPlay={true} muted={!audioEnabled} />
              </div>
            }
            setAudioEnabled={setAudioEnabled}
            audioEnabled={audioEnabled}
            disableVideo={disableVideo}
            enableVideo={enableVideo}
            videoEnabled={videoEnabled}
            videoEnabled={videoEnabled}
          />
        ) : (
          ""
        )}
      </div>
    </div>
  );
};

export default Room;
